select.where:
u.user_id = s.student_id AND s.dorm_id = d.dorm_id
           and building_no = #{buildingNo}
           and room_no = #{roomNo}
           and user_name like concat('%', #{userName}, '%')
           and nick_name like concat('%', #{niceName}, '%')
           and sex = #{sex}
           and bed_No = #{bedNo}
           and dept_Id = #{deptId}
           and province = #{province}
           and stu_Phone = #{stuPhone}
           and fees_category = #{feesCategory}
           and dorm_status = #{dormStatus}
           and cntact_person = #{contactPerson}
           and contact_phone = #{contactPhone}
           and school_roll = #{schoolRoll}
           and school_area = #{schoolArea}

mapper.select:
SELECT  start_time ,end_time
FROM	sys_user
where user_name = #{userName}

SELECT  dept_id
FROM	sys_user
where user_name = #{userName}

SELECT  user_id
FROM	sys_user
where user_name = #{userName}

SELECT  dorm_id
FROM	fzu_dormitory
where building_no = #{buildingNo} and room_no = #{roomNo}

mapper.update:
update fzu_dormitory
building_no = #{buildingNo}, room_no = #{roomNo}, fees_category = #{feesCategory}, dorm_status = #{dormStatus}, contact_person = #{contactPerson}, contact_phone = #{contactPhone},
where dorm_id = #{dormId}

update fzu_student_dormitory
dorm_id = #{dormId}, bed_no = #{bedNo},
where student_id = #{userId}

mapper.insert:
INSERT INTO fzu_student_dormitory (student_id, dorm_id, bed_no) SELECT #{userId}, #{dormId}, #{bedNo}
    WHERE NOT EXISTS (SELECT * FROM fzu_student_dormitory
    WHERE ((dorm_id = #{dormId} AND bed_no = #{bedNo}) OR (student_id = #{userId})

mapper.select:
select student_id as user_id,dorm_id from fzu_student_dormitory

     and student_id = #{userId}
     and dorm_id = #{dormId}
